mapper.select:
SELECT * FROM ins_order 
WHERE user_id = #{userId} 

    AND order_status = #{status}

ORDER BY create_time DESC

SELECT * FROM ins_order 
WHERE 1=1

    AND user_id = #{userId}


    AND product_id = #{productId}


    AND order_no LIKE CONCAT('%', #{orderNo}, '%')


    AND order_status = #{status}

ORDER BY create_time DESC

mapper.update:
UPDATE ins_order 
SET order_status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE ins_order
SET underwriting_status = #{underwritingStatus}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM ins_order WHERE underwriting_status IN
#{status}
ORDER BY create_time DESC
